import React,{useState} from "react";
import AddIcon from '@mui/icons-material/Add';
import Fab from '@mui/material/Fab';
import Zoom from '@mui/material/Zoom'

function CreateArea(props) {
  const [note,setNote]=useState({
    title:"",
    content:""
  })
  function handleTitle(event){
   const {name,value}=event.target;
       setNote(prevNote=> {
        return {
          ...prevNote,
          [name]:value
        }

        })
       }
       function handleSubmit(event){
        event.preventDefault();
        setNote({
          title: "",
          content: ""
          });
       }
  
  return (
    <div>
      <form className="create-note" onSubmit={handleSubmit}>
        <input onChange={handleTitle} value={note.title} name="title" placeholder="Title" />
        <textarea onChange={handleTitle} value={note.content} name="content" placeholder="Take a note..." rows="3" />
        <Zoom></Zoom>
        <Fab onClick={()=> props.onAdd(note)}>< AddIcon/></Fab>
      </form>
    </div>
  );
}

export default CreateArea;
