import React,{useState} from "react";

function CreateArea(props) {
  const [note,setNote]=useState({
    title:"",
    content:""
  })
  function handleTitle(event){
   const {name,value}=event.target;
       setNote(prevNote=> {
        return {
          ...prevNote,
          [name]:value
        }

        })
       }
       function handleSubmit(event){
        event.preventDefault();
        setNote(prevNote=>{
          prevNote.title
        })
       }
  
  return (
    <div>
      <form onSubmit={handleSubmit}>
        <input onChange={handleTitle} value={note.title} name="title" placeholder="Title" />
        <textarea onChange={handleTitle} value={note.content} name="content" placeholder="Take a note..." rows="3" />
        <button onClick={()=> props.onAdd(note)}>Add</button>
      </form>
    </div>
  );
}

export default CreateArea;
